import { useEffect } from "react";
import { useState } from "react";

export default function Chalange(){
const [amount, setAmount] = useState(1);
const [fromCur, setFromCur] = useState('EUR');
const [toCur, setToCur] = useState('USD');
const [result, setResult] = useState("");

    useEffect(function () {
        async function fetchCur() {
          const res = await fetch(
            `https://api.frankfurter.app/latest?amount=${amount}&from=${fromCur}&to=${toCur}`
          );
          const data = await res.json();
          setResult(data.rates[toCur]);

        }
        fetchCur();
      }, []);
      return (
        <div>
          <input type="text" value={amount} onChange={setAmount((e) => e.target.value)} />
          <select value={fromCur} onChange={setFromCur((e) => e.target.value)}>
            <option value="USD">USD</option>
            <option value="EUR">EUR</option>
            <option value="CAD">CAD</option>
            <option value="INR">INR</option>
          </select>
          <select value={toCur} onChange={setToCur((e) => e.target.value)}>
            <option value="USD">USD</option>
            <option value="EUR">EUR</option>
            <option value="CAD">CAD</option>
            <option value="INR">INR</option>
          </select>
          <p>{result} {toCur}</p>
        </div>
      );
}
