import axios from 'axios';

const API = axios.create({
  baseURL: 'http://localhost:5000',
});
export const addFavorite = async (token, movie) => {
  const response = await axios.post('/favorites', movie, {
    headers: { Authorization: `Bearer ${token}` }
  });
  return response.data; // Return the created movie
};

export const deleteFavorite = async (imdbID, token) => {
  return API.delete(`/favorites/${imdbID}`, {
    headers: {Authorization: `Bearer ${token}`},
  })
}

export const getFavorites = async (token) => {
  return API.get('/favorites', {
    headers: {Authorization: `Bearer ${token}`},
  })
}
