import { createContext, useEffect, useState } from "react";
import {useMovies} from './useMovies';
import { useAuth } from "./useAuth";

import {getFavorites, addFavorite, deleteFavorite} from './api';

export const MovieContext = createContext();


export const MovieProvider = function({ children }) {
  const [query, setQuery] = useState("");
  const [selectedId, setSelectedId] = useState(null);

 const {movies,isLoading, error} = useMovies(query);
 const[watched, setWatched] = useState([]);
 const {token} = useAuth();

useEffect(() => {
  const fetchFavorites = async () => {
    if(!token) return;
    
    try {
      const { data } = await getFavorites(token);
      setWatched(data?.savedMovies || data || []); 
    } catch (err) {
      console.error("Fetch error:", err.response?.data || err.message);
    }
  };
  fetchFavorites();
}, [token]);

  function handleSelectMovie(id){
    console.log(id);
    
    setSelectedId((prev) => id === prev ? null : id);
  }
  function handleCloseMovie(){
    setSelectedId(null);
  }

async function handleAddWatched(movie) {
  try {
    const addedMovie = await addFavorite(movie, token);
    
    setWatched(prev => {
      // Prevent duplicates
      if (prev.some(m => m.imdbID === movie.imdbID)) {
        return prev;
     }
      return [...prev, addedMovie || movie];
    });

  } catch (err) {
    console.error("Failed to add movie:", {
      error: err,
      movie: movie.imdbID
    });
    throw err;
  }
}


  async function handleDeleteWatched(movie){
    try{
       await deleteFavorite(token, movie.imdbID);
      setWatched((prev) => prev.filter((m) => m.imdbID !== movie.imdbID));
    }catch(err) {
      console.error("Failed to delete movie from watched", err);
    }
  }
  return(
    <MovieContext.Provider value={({
        query,
        setQuery,
        selectedId,
        setSelectedId,
        movies,
        isLoading,
        error,
        watched,
        handleSelectMovie,
        handleCloseMovie,
        handleAddWatched,
        handleDeleteWatched,
    })}>
        {children}
    </MovieContext.Provider>
  )
}
