import { createContext, useEffect, useState } from "react";
import {useMovies} from './useMovies';
import { useAuth } from "./useAuth";

import {getFavorites, addFavorite, deleteFavorite} from './api';

export const MovieContext = createContext();


export const MovieProvider = function({ children }) {
  const [query, setQuery] = useState("");
  const [selectedId, setSelectedId] = useState(null);

 const {movies,isLoading, error} = useMovies(query);
 const[watched, setWatched] = useState([]);
 const {token} = useAuth();

 useEffect(()=>{
  const fetchFavorites = async () => {
    if(token){
      try{
        const res = await getFavorites(token);
        setWatched(res.data.savedMovies || []);
      }catch(err){
        console.error("Failed to fetch watched movies", err);
      }
    }
  }
  fetchFavorites();
 },[token])

  function handleSelectMovie(id){
    setSelectedId((prev) => id === prev ? null : id);
  }
  function handleCloseMovie(){
    setSelectedId(null);
  }
async function handleAddWatched(movie) {
  try {
    const addedMovie = await addFavorite(token, movie);
    
    setWatched(prev => {
      // Prevent duplicates
      if (prev.some(m => m.imdbID === movie.imdbID)) {
        console.warn('Movie already in watched list');
        return prev;
      }
      return [...prev, addedMovie || movie];
    });

  } catch (err) {
    console.error("Failed to add movie:", {
      error: err,
      movie: movie.imdbID
    });
    throw err;
  }
}
  async function handleDeleteWatched(id){
    try{
       await deleteFavorite(token, id);
      setWatched((prev) => prev.filter((movie) => movie.id !== id));
    }catch(err) {
      console.error("Failed to delete movie from watched", err);
    }
  }
  return(
    <MovieContext.Provider value={({
        query,
        setQuery,
        selectedId,
        setSelectedId,
        movies,
        isLoading,
        error,
        watched,
        handleSelectMovie,
        handleCloseMovie,
        handleAddWatched,
        handleDeleteWatched,
    })}>
        {children}
    </MovieContext.Provider>
  )
}
