import axios from 'axios';

const API = axios.create({
  baseURL: 'http://localhost:5000/api' 
});
export const addFavorite = (movie, token) => {
  return API.post('/favorites', movie, {
    headers: { 
      Authorization: `Bearer ${token}`,
      'Content-Type': 'application/json'
    }
  });
};

export const deleteFavorite = async (token, imdbID) => {
  console.log("Deleting movie with ID:", imdbID);
  const res = await axios.delete(
    `http://localhost:5000/api/favorites/${imdbID}`, // <- correct URL
    {
      headers: {
        Authorization: `Bearer ${token}`, // <- send token in headers
      },
    }
  );
  return res.data;
};
export const getFavorites = (token) => {
  return API.get('/favorites', {
    headers: { Authorization: `Bearer ${token}` }
  });
};


// Add request interceptor
API.interceptors.request.use(config => {
  const token = localStorage.getItem('token');
  
  if (token) {
    config.headers.Authorization = `Bearer ${token}`;
    console.log('Sending token:', token.slice(0, 10) + '...'); // Debug
  } else {
    console.warn('No token available');
  }
  
  return config;
});
