import { useEffect } from "react";

export function useKey(){
useEffect(function(){
    function callback(e){
      if(e.code === 'Escape'){
        onCloseMovie();
      }
    }

    document.addEventListener('keydown', callback);
    return function(){
      document.removeEventListener('keydown', callback);
    }
  }, [onCloseMovie])
}
