import axios from 'axios';

const API = axios.create({
  baseURL: 'http://localhost:5000/api' 
});
export const addFavorite = async (movie, token) => {
  const response = await API.post('/favorites', {
    imdbID: movie.imdbID,
    title: movie.title,
    poster: movie.poster
  }, {
    headers: { 
      'Authorization': `Bearer ${token}`,
      'Content-Type': 'application/json'
    }
  });
  return response.data;
};

export const deleteFavorite = async (token, imdbID) => {
  console.log("Deleting movie with ID:", imdbID);
  const res = await axios.delete(
    `http://localhost:5000/api/favorites/${imdbID}`, // <- correct URL
    {
      headers: {
        Authorization: `Bearer ${token}`, // <- send token in headers
      },
    }
  );
  return res.data;
};

export const getFavorites = async (token) => {
  try {
    const response = await axios.get('http://localhost:5000/api/favorites', {
      headers: {
        'Authorization': `Bearer ${token}`,
        'Content-Type': 'application/json'
      }
    });
    return response.data;
  } catch (err) {
    console.error("API Error:", {
      status: err.response?.status,
      data: err.response?.data,
      token: token ? "Exists" : "Missing"
    });
    throw err;
  }
};

// Add request interceptor
API.interceptors.request.use(config => {
  const token = localStorage.getItem('token');
  
  if (token) {
    config.headers.Authorization = `Bearer ${token}`;
    console.log('Sending token:', token.slice(0, 10) + '...'); // Debug
  } else {
    console.warn('No token available');
  }
  
  return config;
});
