import axios from 'axios';

const API = axios.create({
  baseURL: 'http://localhost:5000',
});
export const addFavorite = async (token, movie) => {
  const response = await axios.post('/favorites', movie, {
    headers: { Authorization: `Bearer ${token}` }
  });
  return response.data; // Return the created movie
};

export const deleteFavorite = async (imdbID, token) => {
  return API.delete(`/favorites/${imdbID}`, {
    headers: {Authorization: `Bearer ${token}`},
  })
}

export const getFavorites = async (token) => {
  return API.get('/favorites', {
    headers: {Authorization: `Bearer ${token}`},
  })
}

API.interceptors.response.use(
  response => response,
  async error => {
    const originalRequest = error.config;
    
    if (error.response?.status === 401 && !originalRequest._retry) {
      originalRequest._retry = true;
      
      try {
        const refreshToken = localStorage.getItem('refreshToken');
        const response = await axios.post('http://localhost:5000/users/refresh', { refreshToken });
        
        localStorage.setItem('token', response.data.token);
        originalRequest.headers.Authorization = `Bearer ${response.data.token}`;
        return API(originalRequest);
      } catch (err) {
        // Redirect to login if refresh fails
        window.location.href = '/login';
        return Promise.reject(err);
      }
    }
    
    return Promise.reject(error);
  }
);
