import axios from 'axios';

const API = axios.create({
  baseURL: 'http://localhost:5000/api' 
});
export const addFavorite = async () => {
const response = await fetch('http://localhost:5000/api/favorites', {
  method: 'GET',
  headers: {
    'Authorization': `Bearer ${localStorage.getItem('token')}`,
    'Content-Type': 'application/json'
  },
  credentials: 'include' // Needed if using cookies
})
  return response.data; // Return the created movie
};

export const deleteFavorite = async (imdbID, token) => {
  return API.delete(`/favorites/${imdbID}`, {
    headers: {Authorization: `Bearer ${token}`},
  })
}

export const getFavorites = async (token) => {
  try {
    const response = await axios.get('http://localhost:5000/api/favorites', {
      headers: {
        'Authorization': `Bearer ${token}`,
        'Content-Type': 'application/json'
      }
    });
    return response.data;
  } catch (err) {
    console.error("API Error:", {
      status: err.response?.status,
      data: err.response?.data,
      token: token ? "Exists" : "Missing"
    });
    throw err;
  }
};

// Add request interceptor
API.interceptors.request.use(config => {
  const token = localStorage.getItem('token');
  
  if (token) {
    config.headers.Authorization = `Bearer ${token}`;
    console.log('Sending token:', token.slice(0, 10) + '...'); // Debug
  } else {
    console.warn('No token available');
  }
  
  return config;
});
