import { Route, Routes} from "react-router-dom";
import MainApp from "./pages/MainApp";
import Login from "./pages/Login";
import Register from "./pages/Register";
import { useContext } from "react";
import { MovieContext } from "./utils/MovieContext";
import { useState, useEffect } from "react";



export default function App() {
  const { isLoading, error, selectedId } = useContext(MovieContext);

  const [isDark, setIsDark] = useState(() => {
    return localStorage.getItem("theme") === "dark";
  });

  useEffect(() => {
    localStorage.setItem("theme", isDark ? "dark" : "light");
    document.body.classList.toggle("dark", isDark);
  }, [isDark]);

  const toggleTheme = () => setIsDark((prev) => !prev);

  // üîê Dummy auth check ‚Äî replace this with your real auth logic
  const isAuthenticated = localStorage.getItem("token");

  return (
    <Routes>
      <Route path="/" element={isAuthenticated ? <MainApp isDark={isDark} toggleTheme={toggleTheme} /> : <Navigate to="/login" />} />
      <Route path="/login" element={<Login />} />
      <Route path="/register" element={<Register />} />
    </Routes>
  );
}
