import { useEffect, useRef, useState } from "react";
import StarRating from "./StarRating";

const tempMovieData = [
  {
    imdbID: "tt1375666",
    Title: "Inception",
    Year: "2010",
    Poster:
      "https://m.media-amazon.com/images/M/MV5BMjAxMzY3NjcxNF5BMl5BanBnXkFtZTcwNTI5OTM0Mw@@._V1_SX300.jpg",
  },
  {
    imdbID: "tt0133093",
    Title: "The Matrix",
    Year: "1999",
    Poster:
      "https://m.media-amazon.com/images/M/MV5BNzQzOTk3OTAtNDQ0Zi00ZTVkLWI0MTEtMDllZjNkYzNjNTc4L2ltYWdlXkEyXkFqcGdeQXVyNjU0OTQ0OTY@._V1_SX300.jpg",
  },
  {
    imdbID: "tt6751668",
    Title: "Parasite",
    Year: "2019",
    Poster:
      "https://m.media-amazon.com/images/M/MV5BYWZjMjk3ZTItODQ2ZC00NTY5LWE0ZDYtZTI3MjcwN2Q5NTVkXkEyXkFqcGdeQXVyODk4OTc3MTY@._V1_SX300.jpg",
  },
];

const tempWatchedData = [
  {
    imdbID: "tt1375666",
    Title: "Inception",
    Year: "2010",
    Poster:
      "https://m.media-amazon.com/images/M/MV5BMjAxMzY3NjcxNF5BMl5BanBnXkFtZTcwNTI5OTM0Mw@@._V1_SX300.jpg",
    runtime: 148,
    imdbRating: 8.8,
    userRating: 10,
  },
  {
    imdbID: "tt0088763",
    Title: "Back to the Future",
    Year: "1985",
    Poster:
      "https://m.media-amazon.com/images/M/MV5BZmU0M2Y1OGUtZjIxNi00ZjBkLTg1MjgtOWIyNThiZWIwYjRiXkEyXkFqcGdeQXVyMTQxNzMzNDI@._V1_SX300.jpg",
    runtime: 116,
    imdbRating: 8.5,
    userRating: 9,
  },
];
const KEY = "63ec1dc6"; 




export default function App() {

  const [query, setQuery] = useState("");
  // const [watched, setWatched] = useState([]);



  const [selectedId, setSelectedId] = useState(null);

  function handleSelectMovie(id){
    setSelectedId(selectedId => id === selectedId ? null : id);
  }
  function handleCloseMovie(){
    setSelectedId(null);
  }

  function handleAddWatched(movie){
    setWatched(watched => [...watched, movie]);
  }
  function handleDeleteWatched(id){
    setWatched(watched => watched.filter((movie) => movie.imdbID !== id));
  }
  useEffect(
    function (){
        localStorage.setItem("watched", JSON.stringify(watched));
    }, [watched]
)






  return (
    <>
    <Navbar>
        <SearchBar query={query} setQuery={setQuery} />
        <NumResults movies={movies} /> 
    </Navbar>
    <Main> 
      <Box>
        {/* {isLoading ? <Loader /> : <MovieList movies={movies} />} */}
        {isLoading && <Loader />}
        {!isLoading && !error && <MovieList movies={movies} onSelectMovie={handleSelectMovie} />}
        {error && <Error message={error} />}
      </Box>
      <Box>
      {
        selectedId ?
         (
         <MovieDetails selectedId={selectedId} onCloseMovie={handleCloseMovie} onAddWatched={handleAddWatched} watched={watched} />
        )
        :
       ( <>
        <WatchedSummary watched={watched} />
        <WatchedMovieList watched={watched} onDeleteWatched={handleDeleteWatched} />
        </>)
        }
      </Box>
    </Main>
    </>
  );
}
const average = (arr) =>
  arr.reduce((acc, cur, i, arr) => acc + cur / arr.length, 0);

function Loader(){
  return <p className="loader">Loading...</p>
}

function Error({message}){
  return <p className="error"><span>‚õî</span>{message}</p>
}
function Navbar({children}){

  return(
    <nav className="nav-bar">
      <Logo />
        {children}
      </nav>
  )
}
function Logo(){
  return(
    <div className="logo">
          <span role="img">üçø</span>
          <h1>usePopcorn</h1>
        </div>
  )
}
function SearchBar({query, setQuery}){
 const inputEl = useRef(null);

 useEffect(function(){

  function callback(e){
    if(document.activeElement === inputEl.current) return;

    if(e.code === "Enter"){
      inputEl.current.focus();
      setQuery("");
    }
  }
  document.addEventListener("keydown", callback);
  return () => document.addEventListener("keydown", callback);

 }, [])
//  useEffect(function(){
//   const el = document.querySelector('.search');
//   console.log(el);
//   el.focus();

//  }, [])

  return(
    <input
    className="search"
    type="text"
    placeholder="Search movies..."
    value={query}
    onChange={(e) => setQuery(e.target.value)}
    ref={inputEl}
  />
  )
}
function NumResults({movies}){
  return(
    <p className="num-results">
          Found <strong>{movies.length}</strong> results
        </p>
  )
}


function Main({children}){


  return(
    <main className="main">
{children}

  </main>
  )
}
function Box({children}){


  const [isOpen, setIsOpen] = useState(true);

  return(
    <div className="box">
    <button
      className="btn-toggle"
      onClick={() => setIsOpen((open) => !open)}
    >
      {isOpen ? "‚Äì" : "+"}
    </button>
 { isOpen && children }
  </div>
  )
}




function MovieList({movies, onSelectMovie}){

  return(

      <ul className="list list-movies">
        {movies?.map((movie) => (
         <Movie movie={movie} key={movie.imdbID} onSelectMovie={onSelectMovie} />
        ))}
      </ul>
  )
}

function Movie({movie, onSelectMovie}){
  return(
    <li onClick={() => onSelectMovie(movie.imdbID)} key={movie.imdbID}>
    <img src={movie.Poster} alt={`${movie.Title} poster`} />
    <h3>{movie.Title}</h3>
    <div>
      <p>
        <span>üóì</span>
        <span>{movie.Year}</span>
      </p>
    </div>
  </li>
  )
}
function MovieDetails({selectedId, onCloseMovie, onAddWatched, watched}){
  const [movie, setMovie] = useState({});
  const [isLoading, setIsLoading]= useState(false);
  const [userRating, setUserRating] = useState('');

  const countRef = useRef(0);

  useEffect(
    function(){

   if(userRating) countRef.current = countRef.current++;

  }, [userRating]
);


  const isWatched = watched.map((movie) => movie.imdbID).includes(selectedId);
  const watchedUserRating = watched.find(movie => movie.imdbID === selectedId)?.userRating;


  const{
    Title: title,
    Year: year,
    Poster: poster,
    imdbRating,
    Runtime: runtime,
    Plot:plot,
    Relased: relased,
    Genre: genre,
    Director: director,
    Actors: actors,
  } = movie;

  function handleAdd(){
    const newWatchedMovie= {
      imdbID:selectedId,
      title,
      year,
      poster,
      imdbRating:Number(imdbRating),
      runtime: Number(runtime.split(" ").at(0)),
      userRating,
      countRatingDecisions: countRef.current,
    }
    onAddWatched(newWatchedMovie);
    onCloseMovie();
  };

  // const [isTop, setIsTop] = useState(

  // );

  useEffect(function(){
    function callback(e){
      if(e.code === 'Escape'){
        onCloseMovie();
      }
    }

    document.addEventListener('keydown', callback);
    return function(){
      document.removeEventListener('keydown', callback);
    }
  }, [onCloseMovie])



useEffect(function(){
  async function getMovieDetails(){
    setIsLoading(true);
    const res =  await fetch(`http://www.omdbapi.com/?apikey=${KEY}&i=${selectedId}`);
    const data = await res.json();
  setMovie(data);
  setIsLoading(false);
  };
  getMovieDetails();

}, [selectedId])

useEffect(function(){
  if(!title) return;
  document.title = `Movie | ${title}`;

  return function(){
    document.title = "usePopcorn";
  }
},
 [title])

return <div className="details">
  {isLoading ? <Loader /> : 
  <> 
   <header>
  <button className="btn-back" onClick={onCloseMovie}>‚¨ÖÔ∏è</button>
    <img src={poster} alt={`Poster of ${movie} movie`} />
    <div className="details-overview">
      <h2>{title}</h2>
      <p>{relased} &bull; {runtime}</p>
      <p>{genre}</p>
      <p><span>‚≠ê </span>{imdbRating} IMDB rating</p>

    </div>
  </header>
  <section>
 <div className="rating"> 
   {!isWatched ? <>
    <StarRating maxRating={10} size={24} onSetRating={setUserRating} />

   { userRating > 0 && <button className="btn-add" onClick={handleAdd}>+ Add to list</button> } </> 
   : <p>You rated this movie {watchedUserRating} <span>‚≠ê</span></p> 
   }

    </div>
    <p><em>{plot}</em></p>
    <p>Starring {actors}</p>
    <p>Directed by {director}</p>
  </section>  
  </>

}


    </div>
}


function WatchedMovieList({watched , onDeleteWatched}){
  return(
    <ul className="list">
    {watched?.map((movie) => (
     <WatchedMovie key={movie.imdbID} movie={movie} onDeleteWatched={onDeleteWatched} />
    ))}
  </ul>
  )
}

function WatchedMovie({movie, onDeleteWatched}){
  return(
    <li key={movie.imdbID}>
    <img src={movie.poster} alt={`${movie.title} poster`} />
    <h3>{movie.title}</h3>
    <div>
      <p>
        <span>‚≠êÔ∏è</span>
        <span>{movie.imdbRating}</span>
      </p>
      <p>
        <span>üåü</span>
        <span>{movie.userRating}</span>
      </p>
      <p>
        <span>‚è≥</span>
        <span>{movie.runtime} min</span>
      </p>
      <button className="btn-delete" onClick={() => onDeleteWatched(movie.imdbID)}>X</button>
    </div>
  </li>
  )
}




function WatchedSummary({watched}){
  const avgImdbRating = average(watched.map((movie) => movie.imdbRating));
  const avgUserRating = average(watched.map((movie) => movie.userRating));
  const avgRuntime = average(watched.map((movie) => movie.runtime));
   return(
    <div className="summary">
    <h2>Movies you watched</h2>
    <div>
      <p>
        <span>#Ô∏è‚É£</span>
        <span>{watched.length} movies</span>
      </p>
      <p>
        <span>‚≠êÔ∏è</span>
        <span>{avgImdbRating.toFixed(2)}</span>
      </p>
      <p>
        <span>üåü</span>
        <span>{avgUserRating.toFixed(2)}</span>
      </p>
      <p>
        <span>‚è≥</span>
        <span>{avgRuntime} min</span>
      </p>
    </div>
  </div>
   )
}
