import { MovieContext } from "../../utils/MovieContext";
import { useContext } from "react";
import styles from "./WatchedSummary.module.css";
import { useAuth } from "../../utils/useAuth";

function WatchedMovie({movie}){
  const { handleDeleteWatched, id } = useContext(MovieContext);
  const {token} = useAuth;

  return(
    <li key={movie.imdbID} className={styles.movie}>
      <img src={movie.poster} alt={`${movie.title} poster`} className={styles.poster} />
      <h3 className={styles.title}>{movie.title}</h3>
      <div className={styles.stats}>
      <p>
        <span>‚≠êÔ∏è</span>
        <span>{movie.imdbRating ? movie.imdbRating : "Unrated"}</span>
      </p>
      <p>
        <span>üåü</span>
        <span>{movie.userRating}</span>
      </p>
      <p>
        <span>‚è≥</span>
        <span>{movie.runtime} min</span>
      </p>
      <button className={styles.btnDelete} onClick={() => handleDeleteWatched(token, id)}>X</button>
    </div>
  </li>
  )
}
export default WatchedMovie;
